# Complete the isValid function below.
def isValid(s):
    l_ch = set(s)
    d = dict()
    for i in s:
        d[i] = d.get(i, 0) + 1
    val = d[next(iter(l_ch))]
    val_2 = None
    for i in l_ch:
        if d[i] == val:
            continue
        if not val_2:
            val_2 = d[i]
        else:
            return 'NO'

    if not val_2:
        return 'YES'

    if val_2 and abs(val - val_2) > 1:
        return 'NO'

    if val == 1 or val_2 == 1:
        return 'YES'

    return 'NO'


if __name__ == '__main__':
    s = ''
    s = 'aaaabc'

    print(isValid(s))
